mapper.select:
# 测验中学生基本信息 WITH stus AS (SELECT ms_id, stu_id, stu_gender, YEAR(cms_created) - pc.cls_entry_year + pc.cls_entry_grade AS grade FROM pt_class_measurement LEFT JOIN pt_class pc on pt_class_measurement.cls_code = pc.cls_code LEFT JOIN pt_student ps on pc.cls_code = ps.cls_code
#{item}
), # 测验科目信息 subs AS (SELECT sub_id, ms_id from pt_subject_subgroup left join pt_measurement on pt_measurement.grp_id = pt_subject_subgroup.grp_id LEFT JOIN pt_subgroup ps on pt_subject_subgroup.grp_id = ps.grp_id
#{item}
), # 各科目评分标准，无评分标准则认为无需测试 pss AS ( SELECT DISTINCT gender, grade, subject_id FROM pt_score_sheet), # 测验中学生需要测验的科目 stusubs AS ( SELECT stu_id, stus.ms_id, sub_id FROM subs INNER JOIN stus ON stus.ms_id = subs.ms_id INNER JOIN pss ON pss.grade = stus.grade AND stu_gender = pss.gender AND subs.sub_id = pss.subject_id) select t1.ms_id, count(t1.stu_id) from (select stu_id, ms_id, count(sub_id) as cnt from stusubs where exists(select stu_id from pt_score where stusubs.stu_id = pt_score.stu_id and pt_score.sub_id = stusubs.sub_id and stusubs.ms_id = pt_score.ms_id) group by stu_id, ms_id) t1 INNER JOIN (select stu_id, ms_id, count(sub_id) as cnt from stusubs group by ms_id, stu_id) t2 ON t1.ms_id = t2.ms_id AND t1.stu_id = t2.stu_id AND t1.cnt = t2.cnt group by t1.ms_id;

WITH stus AS ( SELECT ms_id, stu_id, stu_gender AS gender, YEAR(cms_created) - pc.cls_entry_year + pc.cls_entry_grade AS grade FROM pt_class_measurement INNER JOIN pt_class pc on pt_class_measurement.cls_code = pc.cls_code INNER JOIN pt_student ps on pc.cls_code = ps.cls_code
#{item}
), # 测验科目信息 subs AS (SELECT sub_id, ms_id from pt_subject_subgroup INNER JOIN pt_measurement on pt_measurement.grp_id = pt_subject_subgroup.grp_id INNER JOIN pt_subgroup ps on pt_subject_subgroup.grp_id = ps.grp_id), stusubs AS (SELECT stus.ms_id, gender, grade, sub_id, stus.stu_id FROM subs INNER JOIN stus ON subs.ms_id = stus.ms_id ORDER BY stu_id) SELECT ms_id, COUNT(stu_id) FROM (SELECT ms_id, stu_id FROM stusubs WHERE EXISTS(SELECT sub_id FROM pt_score_sheet pss WHERE pss.grade = stusubs.grade AND pss.gender = stusubs.gender AND pss.subject_id = stusubs.sub_id) GROUP BY ms_id, stu_id) t GROUP BY ms_id;
